#!/usr/bin/python# encoding:utf-8"""模拟浏览器访问网站"""import osimport timefrom selenium import webdriverdriver = webdriver.Chrome('./chromedriver')SITE_LIST = os.path.dirname(__file__) + "/websites.txt"def visit_url(url=None):    if url is None:        return    driver.get(url)    driver.implicitly_wait(30)    # driver.quit()    # time.sleep(10)def main():    if os.path.exists('error_screen'):        pass    else:        os.mkdir('error_screen')    with open(SITE_LIST) as urls:        for url in urls:            if url.startswith("#"):                continue            url = 'http://www.'+url.strip()            visit_url(url)    driver.quit()if __name__ == '__main__':    main()